import React, {Component} from 'react';

class New extends Component {
    constructor(props) {
        super(props)
        this.state = {
            term:'',
            names : [],
          }
        }



          inputHandler = (e) => {
            this.setState(
              {
                term:e.target.value,
              }
            )
            console.log("term", this.state.term)
            console.log("terms", this.state.terms)
            console.log("names", this.state.names)
          }


        // clickToCreateElements
        buttonHandler = (e) => {
          e.preventDefault();
          this.setState({
            term:'',
            names:[...this.state.names,this.state.term]
          })
        }

        render() {
            return (
                <div>
                    <p>haii</p>
                    <input onChange = {this.inputHandler} />
                    <button onClick = {this.buttonHandler}>submit</button>
                    {this.state.names.map(name => <p>{name}</p>)}
                </div>
            )
         }
     }




export default New;
