import './App.css';
// import Person from './Person'


function App () {
  const [state, setState] = useState({
    name : '',
    show : false,
  });


  const inputHandler = (e) => {
    // setState(
    //   {
    //     name:e.target.value,
    //   }
    // )
  }


const buttonHandler = (e) => {
  e.preventDefault();
  setState(
    {
      name: e.target.value
    }
  )
}

  return (
    <div className="App">
      <header className="App-header">
        <h1 onClick = {buttonHandler} >FireBase {state.name}</h1>
        <input className = "int" onChange = {inputHandler}/>
        <button className = "btn" onClick = {buttonHandler} >submit</button>
        <p>{state.name}</p>
      </header>
    </div>
  );
}




export default App;
